package main

import (
	"log"
	"strings"
)

func main() {
	//nolint:lll
	input := ""
	log.Printf("Soluce part 1 is %d", SolvePart01(input))
	log.Printf("Soluce part 2 is %d", SolvePart02(input))
}

func SolvePart01(input string) int {
	inputRune := []rune(input)
	for i := 4; i <= len(inputRune); i++ {
		seq := string(inputRune[i-4 : i])
		if IsUniq(seq) {
			return i
		}
	}
	return 0
}

func SolvePart02(input string) int {
	inputRune := []rune(input)
	for i := 14; i <= len(inputRune); i++ {
		seq := string(inputRune[i-14 : i])
		if IsUniq(seq) {
			return i
		}
	}
	return 0
}

func IsUniq(input string) bool {
	occurrences := ""
	inputRune := []rune(input)
	for i := 0; i < len(inputRune); i++ {
		if strings.Contains(occurrences, string(inputRune[i])) {
			return false
		}
		occurrences += string(inputRune[i])
	}
	return true
}
